package com.orobator.android.scenerecon.view.fragments;import android.app.Fragment;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import com.orobator.android.scenerecon.R;import com.orobator.android.scenerecon.view.activities.SceneReconCameraActivity;public class SetupFragment extends Fragment {    public static final String SCENE_NAME = "scene name";    private EditText mSceneNameEditText;    private Button mCaptureSceneButton;    public View onCreateView(LayoutInflater inflater, ViewGroup parent, Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.fragment_setup, parent, false);        mSceneNameEditText = (EditText) view.findViewById(R.id.scene_name_EditText);        mCaptureSceneButton = (Button) view.findViewById(R.id.capture_scene_Button);        mCaptureSceneButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String sceneName = mSceneNameEditText.getText().toString();                if (sceneName.equals("")) {                    sceneName = "Scene Name can not be blank";                    Toast.makeText(getActivity(), sceneName, Toast.LENGTH_SHORT).show();                    return;                }                Intent i = new Intent(getActivity().getApplicationContext(), SceneReconCameraActivity.class);                i.putExtra(SCENE_NAME, sceneName);                startActivity(i);            }        });        return view;    }}